import{_ as s,c as a,o as n,a as e}from"./app.1df1c4f9.js";const b=JSON.parse('{"title":"\u4EC0\u4E48\u662FWebGL","description":"","frontmatter":{},"headers":[],"relativePath":"articles/webGl\u57FA\u7840.md"}'),l={name:"articles/webGl\u57FA\u7840.md"},p=e(`<h1 id="\u4EC0\u4E48\u662Fwebgl" tabindex="-1">\u4EC0\u4E48\u662FWebGL <a class="header-anchor" href="#\u4EC0\u4E48\u662Fwebgl" aria-hidden="true">#</a></h1><p>\u2014\u2014WebGL\uFF08Web \u56FE\u5F62\u5E93\uFF09\u662F\u4E00\u4E2A JavaScript API\uFF0C\u53EF\u5728\u4EFB\u4F55\u517C\u5BB9\u7684 Web \u6D4F\u89C8\u5668\u4E2D\u6E32\u67D3\u9AD8\u6027\u80FD\u7684\u4EA4\u4E92\u5F0F 3D \u548C 2D \u56FE\u5F62\uFF0C\u800C\u65E0\u9700\u4F7F\u7528\u63D2\u4EF6\u3002WebGL \u901A\u8FC7\u5F15\u5165\u4E00\u4E2A\u4E0E OpenGL ES 2.0 \u975E\u5E38\u4E00\u81F4\u7684 API \u6765\u505A\u5230\u8FD9\u4E00\u70B9\uFF0C\u8BE5 API \u53EF\u4EE5\u5728 HTML5 &lt;canvas&gt;\u5143\u7D20\u4E2D\u4F7F\u7528</p><p>\u2014\u8FD9\u79CD\u4E00\u81F4\u6027\u4F7F API \u53EF\u4EE5\u5229\u7528\u7528\u6237\u8BBE\u5907\u63D0\u4F9B\u7684\u786C\u4EF6\u56FE\u5F62\u52A0\u901F\u3002WebGL\u4F7F\u5F97\u7F51\u9875\u5728\u652F\u6301 HTML &lt;canvas&gt; \u6807\u7B7E\u7684\u6D4F\u89C8\u5668\u4E2D\uFF0C\u4E0D\u9700\u8981\u4F7F\u7528\u4EFB\u4F55\u63D2\u4EF6\uFF0C\u4FBF\u53EF\u4EE5\u4F7F\u7528\u57FA\u4E8E OpenGL ES 2.0 \u7684 API \u5728 canvas \u4E2D\u8FDB\u884C 3D \u6E32\u67D3. WebGL \u7A0B\u5E8F\u7531 javascript \u7684\u63A7\u5236\u4EE3\u7801\uFF0C\u548C\u5728\u8BA1\u7B97\u673A\u7684\u56FE\u5F62\u5904\u7406\u5355\u5143\uFF08GPU, Graphics Processing Unit\uFF09\u4E2D\u6267\u884C\u7684\u7279\u6548\u4EE3\u7801 (shader code\uFF0C\u6E32\u67D3\u4EE3\u7801) \u7EC4\u6210. WebGL \u5143\u7D20\u53EF\u4EE5\u548C\u5176\u4ED6 HTML \u5143\u7D20\u6DF7\u5408\uFF0C\u5E76\u4E14\u4F1A\u548C\u9875\u9762\u7684\u5176\u4ED6\u90E8\u5206\u6216\u9875\u9762\u80CC\u666F\u76F8\u5408\u6210\u3002 #\u51C6\u59073D\u6E32\u67D3 \u9996\u5148\u9700\u8981\u4E00\u4E2A canvas \u5143\u7D20,\u4E0B\u9762\u7684 HTML \u7247\u6BB5\u7528\u6765\u5EFA\u7ACB\u4E00\u4E2A canvas \u5143\u7D20\u5E76\u8BBE\u7F6E\u4E00\u4E2A onload \u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\u6765\u521D\u59CB\u5316\u6211\u4EEC\u7684 WebGL \u4E0A\u4E0B\u6587 \u3002</p><div class="language-"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">&lt;body onload=&quot;main()&quot;&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">  &lt;canvas id=&quot;glcanvas&quot; width=&quot;640&quot; height=&quot;480&quot;&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">    \u4F60\u7684\u6D4F\u89C8\u5668\u4F3C\u4E4E\u4E0D\u652F\u6301\u6216\u8005\u7981\u7528\u4E86 HTML5 &lt;code&gt;&amp;lt;canvas&amp;gt;&lt;/code&gt; \u5143\u7D20\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">  &lt;/canvas&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">&lt;/body&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u5176\u6B21\u51C6\u5907webGL\u4E0A\u4E0B\u6587 \u6211\u4EEC\u7684 JavaScript \u4EE3\u7801\u4E2D\u7684 main() \u51FD\u6570\u5C06\u4F1A\u5728\u6587\u6863\u52A0\u8F7D\u5B8C\u6210\u4E4B\u540E\u88AB\u8C03\u7528\u3002\u5B83\u7684\u4EFB\u52A1\u662F\u8BBE\u7F6E WebGL \u4E0A\u4E0B\u6587\u5E76\u5F00\u59CB\u6E32\u67D3\u5185\u5BB9\u3002</p><div class="language-"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// \u4ECE\u8FD9\u91CC\u5F00\u59CB</span></span>
<span class="line"><span style="color:#A6ACCD;">function main() {</span></span>
<span class="line"><span style="color:#A6ACCD;">    //\u83B7\u53D6canvas\u5143\u7D20</span></span>
<span class="line"><span style="color:#A6ACCD;">  const canvas = document.querySelector(&quot;#glcanvas&quot;);</span></span>
<span class="line"><span style="color:#A6ACCD;">  // \u521D\u59CB\u5316 WebGL \u4E0A\u4E0B\u6587 \u610F\u601D\u5C31\u662F\u83B7\u53D63D\u753B\u5E03</span></span>
<span class="line"><span style="color:#A6ACCD;">  const gl = canvas.getContext(&quot;webgl&quot;);</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  // \u786E\u8BA4 WebGL \u652F\u6301\u6027</span></span>
<span class="line"><span style="color:#A6ACCD;">  if (!gl) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    alert(&quot;\u65E0\u6CD5\u521D\u59CB\u5316 WebGL\uFF0C\u4F60\u7684\u6D4F\u89C8\u5668\u3001\u64CD\u4F5C\u7CFB\u7EDF\u6216\u786C\u4EF6\u7B49\u53EF\u80FD\u4E0D\u652F\u6301 WebGL\u3002&quot;);</span></span>
<span class="line"><span style="color:#A6ACCD;">    return;</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">  // \u4F7F\u7528\u5B8C\u5168\u4E0D\u900F\u660E\u7684\u9ED1\u8272\u6E05\u9664\u6240\u6709\u56FE\u50CF  RGBA</span></span>
<span class="line"><span style="color:#A6ACCD;">  gl.clearColor(0.0, 0.0, 0.0, 1.0);</span></span>
<span class="line"><span style="color:#A6ACCD;">  // \u7528\u4E0A\u9762\u6307\u5B9A\u7684\u989C\u8272\u6E05\u9664\u7F13\u51B2\u533A</span></span>
<span class="line"><span style="color:#A6ACCD;">  gl.clear(gl.COLOR_BUFFER_BIT);</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u540D\u8BCD\u89E3\u91CA <a href="https://developer.mozilla.org/zh-CN/docs/Web/API/HTMLCanvasElement/getContext" target="_blank" rel="noopener noreferrer">getContext</a><a href="https://developer.mozilla.org/zh-CN/docs/Web/API/WebGLRenderingContext" target="_blank" rel="noopener noreferrer">WebGLRenderingContext</a></p><h1 id="\u4F7F\u7528-webgl-\u521B\u5EFA-2d-\u5185\u5BB9" tabindex="-1">\u4F7F\u7528 WebGL \u521B\u5EFA 2D \u5185\u5BB9 <a class="header-anchor" href="#\u4F7F\u7528-webgl-\u521B\u5EFA-2d-\u5185\u5BB9" aria-hidden="true">#</a></h1>`,8),o=[p];function t(c,r,i,C,A,d){return n(),a("div",null,o)}var D=s(l,[["render",t]]);export{b as __pageData,D as default};
